    public static void loadExcelInnerTemp(){
        InputStream inputStream = ResourceUtil.getStream("2024-10-18.xlsx");

        // 创建 ExcelReader，默认读取第一个sheet
        ExcelReader reader = ExcelUtil.getReader(inputStream, 1);

        // 读取为List<Map<String, Object>>，第一行作为表头
        List<Map<String, Object>> readAll = reader.readAll();


        String wicd = "HF-IN";
        String wic = "HF-CO";
        String wiin = "heatflux-cd";
        String wico = "heatflux-c";
        String Time = "Time";

        List<String> title = new ArrayList<>();
        List<Double> wicdList = new ArrayList<>();
        List<Double> wicList = new ArrayList<>();
        List<Double> wiinList = new ArrayList<>();
        List<Double> wicoList = new ArrayList<>();
        SimpleDateFormat sdf = new SimpleDateFormat("HH:mm:ss");

        for (Map<String, Object> row : readAll) {
            System.out.println(row);
            DateTime t = (DateTime) row.get(Time);
            if(t!=null){
                System.out.println(row);
                title.add(t.getHours()+":"+t.getMinutes()+":"+t.getHours());
                wicdList.add(Double.parseDouble(row.get(wicd).toString()));
                wicList.add(Double.parseDouble(row.get(wic).toString()));
                wiinList.add(Double.parseDouble(row.get(wiin).toString()));
                wicoList.add(Double.parseDouble(row.get(wico).toString()));
            }

        }
        System.out.println((new Gson().toJson(title)));
        System.out.println((new Gson().toJson(wicdList)));
        System.out.println((new Gson().toJson(wicList)));
        System.out.println((new Gson().toJson(wiinList)));
        System.out.println((new Gson().toJson(wicoList)));


        reader.close();
    }
    public static void loadExcelPower(){
        InputStream inputStream = ResourceUtil.getStream("2024-10-18.xlsx");

        // 创建 ExcelReader，默认读取第一个sheet
        ExcelReader reader = ExcelUtil.getReader(inputStream, 7);

        // 读取为List<Map<String, Object>>，第一行作为表头
        List<Map<String, Object>> readAll = reader.readAll();


        String Cdte = "CdTe-Pm[W]";
        String CsiPm = "C-si-Pm[W]";
        String Time = "Time";
        List<String> timeList = new ArrayList<>();
        List<Double> CdteList = new ArrayList<>();
        List<Double> CsiPmList = new ArrayList<>();
        SimpleDateFormat sdf = new SimpleDateFormat("HH:mm:ss");
        // 输出每一行
        List<Double> data = new ArrayList<>();
        List<Double> data2 = new ArrayList<>();
        List<String> title = new ArrayList<>();
        for (Map<String, Object> row : readAll) {
            LocalTime t = (LocalTime) row.get(Time);
            title.add(t.getHour()+":"+t.getMinute()+":"+t.getSecond());
           data.add((Double)row.get(Cdte));
           data2.add((Double)row.get(CsiPm));
        }
        System.out.println((new Gson().toJson(title)));
        System.out.println((new Gson().toJson(data)));
        System.out.println((new Gson().toJson(data2)));


        reader.close();
    }
